import React from "react";

const CartReducer = (state, action) => {
  switch (action.type) {
    case "SHOW_CART":
      return { ...state, cartIsShown: true };
    case "CLOSE_CART":
      return { ...state, cartIsShown: false };
    case "INITIATE_DATA":
      return { ...state, meals: action.payload };
    case "ADD_CART":
      return { ...state };
  }
};

export default CartReducer;
